mapper.select:
select * from kpi_notes where 1=1

	and sys_id = #{sysId}
 

	and serial_num = #{serialNum}
 

	and staff_id = #{staffId}
 
order by create_time desc

	limit #{offset},#{pageNumber}

select count(*) from kpi_notes where 1=1

	and sys_id = #{sysId}
 

	and staff_id = #{staffId}
